mapper.select:
SELECT

FROM
sys_organ

  is_deleted = 0
  AND
  is_enable = 1
  AND
  fore_back_type = #{organ.foreBackType,jdbcType=TINYINT}
  
    AND parent_id = #{organ.parentId,jdbcType=INTEGER}
  
  
    AND organ_name LIKE
    CONCAT('%',#{organ.organName,jdbcType=VARCHAR},'%')
  

ORDER BY ${args.sortOrderName}
limit #{args.pageIndex,jdbcType=INTEGER}
,#{args.pageSize,jdbcType=INTEGER};

SELECT COUNT(organ_id) FROM sys_organ
is_deleted = 0 AND is_enable = 1 AND fore_back_type = #{organ.foreBackType,jdbcType=TINYINT}
AND parent_id = #{organ.parentId,jdbcType=INTEGER}
AND organ_name LIKE CONCAT('%',#{organ.organName,jdbcType=VARCHAR},'%')
