mapper.select:
SELECT group_id, group_name, group_description, create_on,is_enable FROM sys_operategroup
is_deleted = 0 AND is_enable = 1 AND fore_back_type = #{operategroup.foreBackType,jdbcType=TINYINT}
AND group_name LIKE CONCAT('%',#{operategroup.groupName,jdbcType=VARCHAR},'%')
ORDER BY ${args.sortOrderName} limit #{args.pageIndex,jdbcType=INTEGER} ,#{args.pageSize,jdbcType=INTEGER};

SELECT COUNT(group_id) FROM sys_operategroup
is_deleted = 0 AND is_enable = 1 AND fore_back_type = #{operategroup.foreBackType,jdbcType=TINYINT}
AND group_name LIKE CONCAT('%',#{operategroup.groupName,jdbcType=VARCHAR},'%')

select
group_id, group_name, create_on
from sys_operategroup
where is_deleted=0 and is_enable=1
and fore_back_type = #{foreBackType,jdbcType=INTEGER}
and group_id = #{groupId,jdbcType=VARCHAR}
